import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import { fetchArticles } from '../api/newsApi';

export const getArticles = createAsyncThunk('articles/getArticles', async ({ category, page }) => {
  const response = await fetchArticles(category, page);
  return response.articles;
});

const articlesSlice = createSlice({
  name: 'articles',
  initialState: {
    articles: [],
    status: null,
    category: '',
    page: 1,
  },
  reducers: {
    setCategory: (state, action) => {
      state.category = action.payload;
      state.page = 1;
    },
    setPage: (state, action) => {
      state.page = action.payload;
    },
  },
  extraReducers: {
    [getArticles.pending]: (state) => {
      state.status = 'loading';
    },
    [getArticles.fulfilled]: (state, action) => {
      state.status = 'success';
      state.articles = action.payload;
    },
    [getArticles.rejected]: (state) => {
      state.status = 'failed';
    },
  },
});

export const { setCategory, setPage } = articlesSlice.actions;
export default articlesSlice.reducer;
