import React from 'react';
import { useState } from 'react';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className="flex justify-between items-center h-16 bg-gradient-to-r from-indigo-500 to-purple-600 shadow-md">
      <div className="container mx-auto px-4 flex items-center">
        {/* Logo */}
        <div className="flex items-center">
          <a className="text-white text-xl font-bold no-underline hover:text-gray-300" href="#">
            My News App
          </a>
        </div>

        {/* Navigation Links (Desktop) */}
        <div className="hidden lg:flex space-x-6">
          <a className="text-white hover:text-gray-300 px-3 py-2 rounded-md font-medium" href="#">
            Home
          </a>
          <a className="text-white hover:text-gray-300 px-3 py-2 rounded-md font-medium" href="#">
            About
          </a>
          <a className="text-white hover:text-gray-300 px-3 py-2 rounded-md font-medium" href="#">
            News
          </a>
        </div>

        {/* Hamburger Menu (Mobile) */}
        <div className="lg:hidden">
          <button onClick={toggleMenu} className="text-white focus:outline-none">
            <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </div>

      {/* Navigation Links (Mobile) */}
      {isOpen && (
        <div className="lg:hidden bg-gray-700 absolute top-full left-0 w-full pb-4">
          <ul className="space-y-2 px-4 py-3">
            <li>
              <a className="text-white block px-3 py-2 rounded-md hover:bg-gray-600 font-medium" href="#">
                Home
              </a>
            </li>
            <li>
              <a className="text-white block px-3 py-2 rounded-md hover:bg-gray-600 font-medium" href="#">
                About
              </a>
            </li>
            <li>
              <a className="text-white block px-3 py-2 rounded-md hover:bg-gray-600 font-medium" href="#">
                News
              </a>
            </li>
          </ul>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
