import React, { useState } from 'react';
import "../Styles/Navbar.css"; // Custom styles if needed

const Navbars = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className="bg-gray-900">
      <div className="container mx-auto px-6 py-3 flex justify-between items-center">
        <div className="text-white text-2xl font-semibold">
          <a className="text-white no-underline hover:text-gray-300" href="#">
            <b className='flex justify-center items-center'>My News-App</b>
          </a>
        </div>
        <div className="hidden lg:flex space-x-4">
          <a className="text-gray-300 hover:text-white py-2 px-4" href="#">Home</a>
          <a className="text-gray-300 hover:text-white py-2 px-4" href="#">About</a>
          <a className="text-gray-300 hover:text-white py-2 px-4" href="#">News</a>
        </div>
        <div className="lg:hidden">
          <button onClick={toggleMenu} className="text-gray-300 focus:outline-none">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16"></path>
            </svg>
          </button>
        </div>
      </div>
      {isOpen && (
        <div className="lg:hidden px-2 pt-2 pb-3 space-y-1">
          <a className="block text-gray-300 hover:text-white py-2 px-4" href="#">Home</a>
          <a className="block text-gray-300 hover:text-white py-2 px-4" href="#">About</a>
          <a className="block text-gray-300 hover:text-white py-2 px-4" href="#">News</a>
        </div>
      )}
    </nav>
  );
};

export default Navbars;
