import React from 'react';
import { useDispatch } from 'react-redux';
import { setCategory } from '../redux/articleSlice';

const CategoryFilter = () => {
  const dispatch = useDispatch();
  const categories = ['Business', 'Technology', 'Entertainment', 'Health', 'Science', 'Sports'];

  return (
    <div className="flex flex-wrap justify-center gap-4 p-4 bg-gray-100 rounded-md">
      {categories.map((category) => (
        <button
          key={category}
          onClick={() => dispatch(setCategory(category.toLowerCase()))}
          className="px-4 py-2 bg-gray-900 text-white rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500"
        >
          {category}
        </button>
      ))}
    </div>
  );
};

export default CategoryFilter;
