import axios from 'axios';

const API_KEY = '335163870f74471dae41f91c0a909786'; // Replace with your News API key
const BASE_URL = 'https://newsapi.org/v2';

export const fetchArticles = async (category = '', page = 1) => {
  try {
    const url = `${BASE_URL}/top-headlines?country=us${category ? `&category=${category}` : ''}&page=${page}&apiKey=${API_KEY}`;
    // console.log("Request URL:", url); // Log the URL for debugging
    const response = await axios.get(url);
    return response.data.articles; // Return only the articles array from the response data
  } catch (error) {
    console.error("Error fetching articles:", error);
    if (error.response) {
      console.error("Response data:", error.response.data);
      console.error("Response status:", error.response.status);
      console.error("Response headers:", error.response.headers);
    }
    throw error; // Re-throw the error to handle it in the component
  }
};
