import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { setPage } from '../redux/articleSlice';

const Pagination = () => {
  const dispatch = useDispatch();
  const page = useSelector((state) => state.articles.page);

  return (
    <div className="flex justify-center items-center space-x-4 mt-6">
      <button 
        onClick={() => dispatch(setPage(page - 1))} 
        disabled={page === 1}
        className="px-4 py-2 bg-gray-900 text-white rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500 disabled:bg-gray-600"
      >
        Previous
      </button>
      <span className="text-gray-700">Page {page}</span>
      <button 
        onClick={() => dispatch(setPage(page + 1))}
        className="px-4 py-2 bg-gray-900 text-white rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500"
      >
        Next
      </button>
    </div>
  );
};

export default Pagination;
